import React from "react";
import { useEffect } from "react";
import { useState, useRef } from "react";
import { Fragment } from "react";

const MyForm = () => {
  const [name, setName] = useState();
  const refName = useRef();
  const refSetName = useRef();

  useEffect(() => {
    refName.current = name
  }, [name])

  refSetName = setName((fName) => {
    fName.target.value = name
  })

  const handleSubmit = () => {};
  return (
    <Fragment>
      <h1>My Form</h1>
      <form >
        <input 
            type="text" 
            name="firstName" 
            placeholder="First name" />
            onChange = {refSetName}

        <input type="button" value="SUBMIT" onChange={handleSubmit}/>
      </form>
    </Fragment>
  );
};

export default MyForm;
